import React from "react";
import { Link, useLocation } from "react-router-dom";
import { Home, Library, Sparkles } from "lucide-react";

const navigationItems = [
  { title: "Home", url: "/home", icon: Home },
  { title: "Dashboard", url: "/dashboard", icon: Library },
];

export default function Layout({ children }) {
  const location = useLocation();
  const showSidebar = location.pathname !== '/home';

  if (!showSidebar) {
    return <main data-filename="pages/CodeExporter" data-linenumber="94" data-visual-selector-id="pages/CodeExporter94">{children}</main>;
  }

  return (
    <div data-filename="pages/CodeExporter" data-linenumber="98" data-visual-selector-id="pages/CodeExporter98" className="min-h-screen flex">
