const fs = require('fs');
const path = require('path');
const projectData = require('./flashgenius_code.json');

const outputDir = 'flashgenius-standalone';

if (fs.existsSync(outputDir)) {
  console.log(`Directory '${outputDir}' already exists. Please remove it and try again.`);
  process.exit(1);
}

fs.mkdirSync(outputDir, { recursive: true });

Object.entries(projectData).forEach(([filePath, content]) => {
  const fullPath = path.join(outputDir, filePath);
  const dirName = path.dirname(fullPath);

  if (!fs.existsSync(dirName)) {
    fs.mkdirSync(dirName, { recursive: true });
  }

  fs.writeFileSync(fullPath, content);
});

console.log(`Project unpacked successfully into '${outputDir}' directory!`);
console.log('Next steps:');
console.log(`1. cd ${outputDir}`);
console.log('2. npm install');
console.log('3. npm start (to run locally)');
console.log('4. Push the contents of this folder to your GitHub repository.');

